// Process current scan results
const currentResults = $input.item(0).json;
const currentPorts = [];

// Extract current open ports
for (const result of currentResults) {
  if (result.status === 'Open') {
    currentPorts.push({
      port: result.port,
      protocol: result.protocol
    });
  }
}

// Set previous scan results as a node variable to compare later
$node.previousPorts = JSON.stringify(currentPorts);

return [{json: {currentPorts}}];

// Compare with previous scan if available
let previousPorts = [];
if ($node.previousPorts) {
  previousPorts = JSON.parse($node.previousPorts);
}

const currentPorts = $input.item(0).json.currentPorts;

// Find new ports
const newPorts = currentPorts.filter(current => {
  return !previousPorts.some(prev => 
    prev.port === current.port && prev.protocol === current.protocol
  );
});

// Find closed ports
const closedPorts = previousPorts.filter(prev => {
  return !currentPorts.some(current => 
    current.port === prev.port && current.protocol === prev.protocol
  );
});

return [{
  json: {
    newPorts,
    closedPorts,
    hasChanges: newPorts.length > 0 || closedPorts.length > 0
  }
}];
